// Ciclos prioritários
export const CICLOS_PRIORITARIOS = ["CICLO 001", "CICLO 002", "CICLO 003"];

export const isPrioridade = (ciclo: string): boolean => 
  CICLOS_PRIORITARIOS.includes(ciclo);

export const getTipoIntervencao = (ciclo: string): TipoIntervencao => {
  if (["CICLO 001", "CICLO 002", "CICLO 003"].includes(ciclo)) {
    return "IMPLANTAÇÃO";
  }
  return "MODERNIZAÇÃO";
};

