// Função para obter escolas por ciclo
export const getEscolasPorCiclo = (ciclo: string): EscolaCronograma[] => {
  return escolasCronograma.filter(escola => escola.cicloAtendimento === ciclo);
};

// Função para obter estatísticas por GRE
export const getEstatisticasPorGRE = (gre: string) => {
  const escolas = escolasCronograma.filter(escola => escola.gre === gre);
  return {
    total: escolas.length,
    urbana: escolas.filter(e => e.localizacao === "URBANA").length,
    rural: escolas.filter(e => e.localizacao === "RURAL").length,
    conformes: escolas.filter(e => e.statusMeta2026 === "CONFORME").length,
    naoConformes: escolas.filter(e => e.statusMeta2026 === "NÃO CONFORME").length
  };
};

// Função para obter estatísticas por município
export const getEstatisticasPorMunicipio = (municipio: string) => {
  const escolas = escolasCronograma.filter(escola => escola.municipio === municipio);
  return {
    total: escolas.length,
    escolas: escolas.map(e => e.nomeEscola)
  };
};

export default escolasCronograma;
